import { Container, Col, Row, Button } from 'react-bootstrap';


function Banner() {
    return ( 
        <>
         <Container
        fluid
        style={{
          height: "40rem",
          width: "100%",
          backgroundRepeat: "no-repeat",
          padding: "0px",
          marginRight:"0px",
          objectFit: "cover",
          backgroundColor:"#000",
          backgroundImage:
            "url(https://images.pexels.com/photos/1649771/pexels-photo-1649771.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1)",
        }}
      >
        {/* <Image style={{height:"100%" ,width: "100%", objectFit:"cover"}} src="https://images.pexels.com/photos/773063/pexels-photo-773063.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"/> */}
        <Container
          fluid
          style={{
            background: "rgba(0, 0, 0, 0.6)",
            height: "30rem",
            justifyContent: "center",
          }}
        >
          <Row className="justify-content-md-center">
            <Col></Col>
            <Col sm="auto" md="auto" lg="auto" style={{ marginTop: "5rem" }}>
              <h1
                style={{
                  fontSize: "56px",
                  color: "white",
                  justifyContent: "center",
                }}
              >
                Sports Store Dapp
              </h1>
              <Button style={{ width: "450px" }} variant="light">
                Shop Now.
              </Button>
            </Col>
            <Col></Col>
          </Row>
          <Row  style={{marginBottom:"50px"}} className="justify-content-md-center ">
        <Col></Col>
       <Col></Col>
       </Row>
        </Container>
        </Container>

        </>
     );
}

export default Banner;
