import { Container, Row, Col, Card, Button } from 'react-bootstrap';
import Sounds from "../Utils/Speakers.json";

function Speakers() {


    return ( 
        <>
        <Container style={{marginTop:"50px"}}>
            {Sounds.map((product) => {
                return (
                    <Row key={product.id} style={{marginTop:"20px"}}>
                    <Col style={{
                        "margin":"0",
                        top:"50%",
                        display:"flex",
                        justifyContent:"center",
                        alignItems:"center"
                    }}>
                    <Card style={{ width: '35rem' }}>
                     <Card.Img 
                     variant="top" 
                     style={{objectFit:"cover"}}
                     src={product.imageURL} />
                 </Card>
                    </Col>
                    <Col style={{
                        "margin":"0",
                        top:"50%",
                        display:"flex",
                        justifyContent:"center",
                        alignItems:"center"
                    }}>

                    <p> 
                    <h2 style={{marginBottom:"15px"}}>{product.name}</h2>
                    {product.description}
                    <br />
                    <Button variant='light' size="lg" style={{background:"#d87d4a", marginTop:"20px", color:"#fff"}}> {product.price} ETH</Button>
                    </p>
                    </Col>
                </Row>
                )
            })}

        </Container>
        </>
     );
}

export default Speakers;
